    echo "Это платный аккаунт\n";
}

// Использование в массиве
$accountTypes = [
    AccountType::FREE,
    AccountType::PAID,
    AccountType::UNLIMITED
];

echo "Типы аккаунтов:\n";
foreach ($accountTypes as $type) {
    echo "- {$type-&gt;getDisplayName()}\n";
}

// Фильтрация по типам
