$unlimitedAccounts = array_filter($accountTypes, fn($t) =&gt; $t-&gt;isUnlimited());

echo "Бесплатные аккаунты: " . count($freeAccounts) . "\n";
echo "Безлимитные аккаунты: " . count($unlimitedAccounts) . "\n";

