        if ($stats['total'] &gt; 0) {
            $filmsPercent = round(($stats['films'] / $stats['total']) * 100, 1);
            $seriesPercent = round(($stats['series'] / $stats['total']) * 100, 1);
            $otherPercent = round(($stats['other'] / $stats['total']) * 100, 1);

