            $type = ContentType::from($typeValue);
            $icon = getContentTypeIcon($type);
            $displayName = $type-&gt;getDisplayName();
            $percent = round(($count / $stats['total']) * 100, 1);

            $report .= "{$icon} {$displayName}: {$count} ({$percent}%)\n";
