        $report .= "\n";

        // Фильмы
        $films = $this-&gt;getContentByType(ContentType::FILM);
        if (!empty($films)) {
            $report .= "🎬 ФИЛЬМЫ (" . count($films) . " элементов):\n";
            foreach (array_slice($films, 0, 5) as $film) {
                $report .= "• {$film-&gt;getDisplayName()}\n";
            }
            if (count($films) &gt; 5) {
                $report .= "... и еще " . (count($films) - 5) . " фильмов\n";
            }
            $report .= "\n";
