            $icon = getRelationTypeIcon($type);
            $description = $type-&gt;getDescription();

            $report .= "🎬 {$icon} {$description} (" . count($films) . " фильмов):\n";
            foreach ($films as $film) {
                $report .= "  • {$film-&gt;getDisplayName()}\n";
