        ReviewOrder::USER_NEGATIVE_RATING_ASC =&gt; 'Отзывы отсортированы по количеству отрицательных оценок (по возрастанию)',
        ReviewOrder::USER_NEGATIVE_RATING_DESC =&gt; 'Отзывы отсортированы по количеству отрицательных оценок (по убыванию)'
    };
}

// Функция для группировки типов сортировки
function groupReviewOrders(): array {
    $groups = [
