    'nameRu' =&gt; 'Киану Ривз',
    'nameEn' =&gt; 'Keanu Reeves'
];

// Создание моделей
$film = createModelFromArray(Film::class, $filmData);
$person = createModelFromArray(Person::class, $personData);

echo "Фильм: {$film-&gt;getDisplayName()}\n";
echo "Персона: {$person-&gt;getDisplayName()}\n";

// Создание массива моделей
