    echo "Рентабельность: {$analysis['profitability']}%\n";
}

echo "\nСтатистика по валютам:\n";
foreach ($analysis['currencyBreakdown'] as $currency =&gt; $data) {
    echo "- {$currency}: {$data['count']} записей, {$data['symbol']}" . number_format($data['total']) . "\n";
}
