        $report .= "🏆 ТОП-15 ЖАНРОВ:\n";
        foreach (array_slice($stats, 0, 15, true) as $genre =&gt; $count) {
            $percentage = round(($count / $totalFilms) * 100, 1);
            $report .= "• {$genre}: {$count} фильмов ({$percentage}%)\n";
        }

        $report .= "\n🎭 ПОПУЛЯРНЫЕ ЖАНРОВЫЕ КОМБИНАЦИИ:\n";
