    if (!empty($analysis['nameAnalysis']['nameLengths'])) {
        $analysis['nameAnalysis']['averageNameLength'] = round(array_sum($analysis['nameAnalysis']['nameLengths']) / count($analysis['nameAnalysis']['nameLengths']));
