        // Элементы
        if (!$this-&gt;response-&gt;isEmpty()) {
            $html .= "&lt;div class='section'&gt;\n";
            $html .= "&lt;div class='section-title'&gt;📋 Элементы текущей страницы&lt;/div&gt;\n";
            $html .= "&lt;div class='item-grid'&gt;\n";

            foreach ($this-&gt;response-&gt;items as $item) {
